mapper.select:
SELECT * 
FROM GOODS

WHERE goods_type = #{goods_type}

ORDER BY ${goods_sort}

SELECT			count(*)
FROM			Member
WHERE			member_id = #{member_id} AND password = #{password}

SELECT count(*)
FROM Cart
WHERE member_id = #{member_id} AND goods_id = #{goods_id}

mapper.update:
UPDATE Cart
SET c_amount = c_amount + #{c_amount}
WHERE member_id = #{member_id} AND goods_id = #{goods_id}
